                // Update mood text based on LED count
                const moodMap = ['Calm', 'Happy', 'Excited', 'Anxious', 'Sad', 'Tired', 'Irritated', 'Angry', 'Depressed', 'Stressed'];
                currentState.mood = moodMap[nextIndex] || 'None';

                // Publish to MQTT
                msg.mqtt = {
                    topic: 'sensor/mood',
                    payload: {
                        timestamp: new Date().toISOString(),
                        level: nextIndex + 1,
                        mood: currentState.mood,
                        leds: currentState.mood_leds
                    }
                };
            }
        } else if (msg.payload.action === 'reset') {
            currentState.mood_leds = Array(10).fill(0);
            currentState.mood = 'None';

            // Publish reset to MQTT
            msg.mqtt = {
                topic: 'sensor/mood',
                payload: {
                    timestamp: new Date().toISOString(),
                    level: 0,
                    mood: 'None',
                    leds: currentState.mood_leds
                }
            };
        }
    } else {
        // Handle hardware MQTT messages
        currentState.mood_leds = msg.payload.leds;
        currentState.mood = msg.payload.mood;
    }
}

