// Predictive LED ring display logic

function getPeriodColor(dayInCycle, periodLength = 6) {
    if (dayInCycle <= periodLength) return '#f47c7c';    // Menstrual period - soft red
    if (dayInCycle <= 13) return '#a1de93';              // Follicular phase - soft green
    if (dayInCycle <= 16) return '#f7f48b';              // Ovulation phase - soft yellow
    return '#70a1d7';                                    // Luteal phase - soft blue
}

function calculateCycleDay(records) {
    if (!records || records.length === 0) {
        return { cycleDay: 0, hasValidData: false };
    }

    // Sort by date descending
    records.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const lastStart = records.find(r => r.button_status === 'start');
    const lastEnd = records.find(r => r.button_status === 'end');

    if (!lastStart && !lastEnd) {
        return { cycleDay: 0, hasValidData: false };
    }

    if (lastStart) {
        const startDate = new Date(lastStart.date);
        startDate.setHours(0, 0, 0, 0);
        const hasEndAfterStart = lastEnd && new Date(lastEnd.date).getTime() > startDate.getTime();

        if (!hasEndAfterStart) {
            const daysSinceStart = Math.floor((today.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24)) + 1;
            return {
                cycleDay: daysSinceStart,
                hasValidData: true,
                inPeriod: true
            };
        }
    }

    if (lastEnd && (!lastStart || new Date(lastEnd.date).getTime() > new Date(lastStart.date).getTime())) {
        const endDate = new Date(lastEnd.date);
        endDate.setHours(0, 0, 0, 0);
        const daysSinceEnd = Math.floor((today.getTime() - endDate.getTime()) / (1000 * 60 * 60 * 24));
        return {
            cycleDay: daysSinceEnd + 7,  // Assume follicular starts 1 day after end
            hasValidData: true,
            inPeriod: false
        };
    }

    return { cycleDay: 0, hasValidData: false };
}

// Fetch stored records
let records = flow.get('records') || [];

// Compute current cycle info
let cycleInfo = calculateCycleDay(records);

if (!cycleInfo.hasValidData) {
    msg.payload = {
        leds: Array(20).fill('#000000'),
        has_prediction: false
    };
    return msg;
}

// Generate prediction for next 20 days
let leds = [];
for (let i = 0; i < 20; i++) {
    let futureDay = ((cycleInfo.cycleDay + i - 1) % 28) + 1;

    if (cycleInfo.inPeriod && i === 0) {
        leds.push(getPeriodColor(cycleInfo.cycleDay));
    } else {
        leds.push(getPeriodColor(futureDay));
    }
}

msg.payload = {
    leds: leds,
    has_prediction: true,
    current_cycle_day: cycleInfo.cycleDay,
    in_period: cycleInfo.inPeriod
};

return msg;

// Initialize movement state if not exists
let movementState = flow.get('movement_state') || {
    isMoving: false,
    pressStartTime: 0,
    pressEndTime: 0
};

if (msg.topic === 'robot_control' && msg.payload) {
    const { action, state } = msg.payload;
    const currentTime = Date.now();

    // Handle button release - stop the robot
    if (state === 'release') {
        movementState.pressEndTime = currentTime;
        const pressDuration = movementState.pressEndTime - movementState.pressStartTime;

        // For all actions, stop movement when button is released
        msg.payload = { linear: 0.0, angular: 0.0 };
        movementState.isMoving = false;

        // For left/right turns with short press (less than 500ms)
        // We don't need special handling as the turn has already been executed

        flow.set('movement_state', movementState);
        msg.topic = 'cmd_vel';
        return msg;
    }

    // Handle button press
    if (state === 'press') {
        movementState.isMoving = true;
        movementState.pressStartTime = currentTime;

        switch(action) {
            case 'forward':
                // Forward movement - maintain current orientation
                msg.payload = { linear: 0.2, angular: 0.0 };
                break;

            case 'backward':
                // Backward movement - maintain current orientation
                msg.payload = { linear: -0.2, angular: 0.0 };
                break;

            case 'left':
                // Left turn - only angular velocity
                msg.payload = { linear: 0.0, angular: 0.5 };
                break;

            case 'right':
                // Right turn - only angular velocity
                msg.payload = { linear: 0.0, angular: -0.5 };
                break;
        }

        flow.set('movement_state', movementState);
        msg.topic = 'cmd_vel';
        return msg;
    }
}

return null;

