const fs = require('fs');
const path = require('path');
const os = require('os');

// Use absolute path
const DATA_DIR = path.join(os.homedir(), '.node-red', 'data');
const RECORDS_FILE = path.join(DATA_DIR, 'records.json');

// Ensure data directory exists
if (!fs.existsSync(DATA_DIR)) {
    fs.mkdirSync(DATA_DIR);
}

// Read data
function loadRecords() {
    try {
        if (fs.existsSync(RECORDS_FILE)) {
            const data = fs.readFileSync(RECORDS_FILE, 'utf8');
            const records = JSON.parse(data);
            node.log(`Successfully loaded ${records.length} records from file`);
            return records;
        }
    } catch (error) {
        node.error(`Error loading records: ${error.message}`);
    }
    return [];
}

// Save data
function saveRecords(records) {
    try {
        fs.writeFileSync(RECORDS_FILE, JSON.stringify(records, null, 2));
        node.log(`Successfully saved ${records.length} records to file`);
    } catch (error) {
        node.error(`Error saving records: ${error.message}`);
    }
}

// Export functions for use by other nodes
global.set('loadRecords', loadRecords);
global.set('saveRecords', saveRecords);

// Return empty message on first run
return null;

// Load data when flow starts
const loadRecords = global.get('loadRecords');
if (loadRecords) {
    const records = loadRecords();
    flow.set('records', records);
    node.log('Records loaded from file');
}

return null;
